import React from "react";

const videoQualityOptions = ["1080", "720", "360", "240"];

function VideoControls(props) {
    const qualityOptions = videoQualityOptions.map((quality, index) => {
        return (
            <input
                key={index}
                type="radio"
                value={quality}
                onChange={props.handleVideoQualityChange}
                checked={props.streamerConfig.quality === quality}
            />
        );
    });

    return (
        <div className="vimeo-controls">
            <button onClick={props.setVideoPlay}>Play</button>
            <button onClick={props.setVideoPause}>Pause</button>
            <div className="quality-option">
                <input
                    id="quality-1080"
                    type="radio"
                    value="1080"
                    onChange={props.handleVideoQualityChange}
                    checked={props.streamerConfig.quality === "1080"}
                />
                <label htmlFor="quality-1080">1080p</label>
            </div>
            <div className="quality-option">
                <input
                    id="quality-720"
                    type="radio"
                    value="720"
                    onChange={props.handleVideoQualityChange}
                    checked={props.streamerConfig.quality === "720"}
                />
                <label htmlFor="quality-720">720p</label>
            </div>
            <div className="quality-option">
                <input
                    id="quality-360"
                    type="radio"
                    value="360"
                    onChange={props.handleVideoQualityChange}
                    checked={props.streamerConfig.quality === "360"}
                />
                <label htmlFor="quality-360">360p</label>
            </div>
            <div className="quality-option">
                <input
                    id="quality-240"
                    type="radio"
                    value="240"
                    onChange={props.handleVideoQualityChange}
                    checked={props.streamerConfig.quality === "240"}
                />
                <label htmlFor="quality-240">240p</label>
            </div>
        </div>
    );
}

export default VideoControls;
