import { Grid, Button, Switch, IconButton } from "@mui/material";
import React from "react";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import PauseIcon from "@mui/icons-material/Pause";

function VideoControls(props) {
    const videoQualityOptions = ["1440", "1080", "720", "360", "240"];

    const qualityOptions = videoQualityOptions.map(quality => {
        return (
            <div className="video-options">
                <input
                    key={quality}
                    id={`video-quality-${quality}`}
                    type="radio"
                    value={quality}
                    onChange={props.handleVideoQualityChange}
                    checked={props.streamerConfig.quality === quality}
                />
                <label htmlFor={`video-quality-${quality}`}>
                    {`${quality}p`}
                </label>
            </div>
        );
    });

    return (
        <Grid container>
            <Grid item container>
                <IconButton color="primary" onClick={props.toggleVideoPlay}>
                    {props.streamerConfig.pause ? (
                        <PlayArrowIcon />
                    ) : (
                        <PauseIcon />
                    )}
                </IconButton>
                <IconButton color="primary" onClick={props.setVideoPause}>
                    <PauseIcon />
                </IconButton>
            </Grid>
            <Grid item container>
                <Switch
                    size="small"
                    onChange={props.toggleVideoLoop}
                    checked={props.streamerConfig.loop}
                />
                {qualityOptions}
            </Grid>
        </Grid>
    );
}

export default VideoControls;
