import {
    Grid,
    Button,
    Switch,
    FormControl,
    FormControlLabel,
    Radio,
    RadioGroup,
    FormLabel,
    FormGroup,
} from "@mui/material";
import React from "react";
import PlayArrowIcon from "@mui/icons-material/PlayArrow";
import PauseIcon from "@mui/icons-material/Pause";

function VideoControls(props) {
    const videoQualityOptions = ["1080", "720", "360", "240"];

    const qualityOptions = videoQualityOptions.map(quality => {
        return (
            <FormControlLabel
                key={quality}
                value={quality}
                control={<Radio />}
                label={`${quality}p`}
            />
        );
    });

    return (
        <Grid
            container
            justifyContent={"space-between"}
            alignItems={"center"}
            spacing={2}
        >
            <Grid item xs="auto">
                <Button
                    id="video-play-button"
                    variant="contained"
                    color="primary"
                    onClick={props.toggleVideoPlay}
                >
                    {props.streamerConfig.paused ? (
                        <PlayArrowIcon />
                    ) : (
                        <PauseIcon />
                    )}
                </Button>
            </Grid>
            <Grid
                item
                container
                xs
                justifyContent={"space-between"}
                alignItems={"center"}
            >
                <Grid item xs="auto">
                    <FormGroup>
                        <FormLabel>Loop</FormLabel>
                        <FormControlLabel
                            control={
                                <Switch
                                    size="large"
                                    onChange={props.toggleVideoLoop}
                                    checked={props.streamerConfig.loop}
                                />
                            }
                            label={
                                props.streamerConfig.loop
                                    ? "On loop"
                                    : "Off loop"
                            }
                        />
                    </FormGroup>
                </Grid>
                <Grid container item xs justifyContent={"flex-end"}>
                    <FormControl>
                        <FormLabel id="video-playback-quality">
                            Quality options
                        </FormLabel>
                        <RadioGroup
                            row
                            value={props.streamerConfig.quality}
                            onChange={props.handleVideoQualityChange}
                        >
                            {qualityOptions}
                        </RadioGroup>
                    </FormControl>
                </Grid>
            </Grid>
        </Grid>
    );
}

export default VideoControls;
