import React, { useEffect, useState, useRef } from "react";
import Vimeo from "@u-wave/react-vimeo";
import VideoForm from "./VideoForm";

function VideoStreamer() {
    const [isloadDisabled, setIsloadDisabled] = useState(
        true // disable load button by default
    );

    const [videoFormConfig, setVideoFormConfig] = useState({
        videoUrl: "", // Set default video URL to empty string
        quality: "1080p", // Set default quality to 1080p
    });

    const [streamerConfig, setStreamerConfig] = useState({
        videoId: "712561471", // Set default video to 712561471
        quality: "1080", // Set default quality to 1080p
        paused: true, // Set default state to paused
    });

    const vimeoRef = useRef();

    const [id, setId] = useState("1234");

    function setVideoPlay() {
        setStreamerConfig(prevState => {
            return {
                ...prevState,
                paused: false,
            };
        });
    }

    function setVideoPause() {
        setStreamerConfig(prevState => {
            return {
                ...prevState,
                paused: true,
            };
        });
    }

    function handleFormChange(event) {
        const { name, value } = event.target;

        // Update the form state.
        setVideoFormConfig(prevState => ({
            ...prevState,
            [name]: value,
        }));

        // if the video id is not empty, enable the load button
        if (name === "videoUrl" && !value) {
            setIsloadDisabled(true);
        } else if (name === "videoUrl" && value) {
            setIsloadDisabled(false);
        }
    }

    function handleFormSubmit(event) {
        event.preventDefault();

        console.log(videoFormConfig.quality);

        // Update the streamer config.
        setStreamerConfig(prevState => ({
            ...prevState,
            videoId: videoFormConfig.videoUrl,
            quality: videoFormConfig.quality,
        }));

        setId(Math.random().toString());
    }

    console.log(id);
    return (
        <div>
            <VideoForm
                videoFormConfig={videoFormConfig}
                isloadDisabled={isloadDisabled}
                handleFormChange={handleFormChange}
                handleFormSubmit={handleFormSubmit}
            />
            <Vimeo
                key={id}
                ref={vimeoRef}
                video={streamerConfig.videoId}
                quality={streamerConfig.quality}
                paused={streamerConfig.paused}
                width="640"
            />
            <button onClick={setVideoPlay}>Play</button>
            <button onClick={setVideoPause}>Pause</button>
        </div>
    );
}

export default VideoStreamer;
